UPDATE TRANSCRIPT_MESSAGE SET GRAD_PROJECTED_MESSAGE='Based on the information provided by the school, this student is projected to graduate in the %s',HONOUR_PROJECTED_NOTE='Based on the information provided by the school, this student is projected to graduate in the %s with Honours.' WHERE GRADUATION_PROGRAM_CODE != 'SCCP' AND MESSAGE_TYPE_CODE = 'GRADUATED';
UPDATE TRANSCRIPT_MESSAGE SET GRAD_PROJECTED_MESSAGE='Based on the information provided by the school, this student is not projected to graduate in the %s.' WHERE GRADUATION_PROGRAM_CODE != 'SCCP' AND MESSAGE_TYPE_CODE = 'NOT_GRADUATED';
UPDATE TRANSCRIPT_MESSAGE SET GRAD_MAIN_MESSAGE = 'Upon completion of this student''s School Completion Certificate Program, a certificate will be issued from the Ministry of Education for meeting their educational program goals other than graduation, as determined by the board of education.' WHERE GRADUATION_PROGRAM_CODE = 'SCCP' AND MESSAGE_TYPE_CODE = 'NOT_GRADUATED';